import React, { useState, useEffect } from 'react';
import BookList from '../components/BookList';
import './BooksPage.css';

const BooksPage = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  // Sample data - In a real application, this would come from an API
  useEffect(() => {
    // Simulating API call
    setTimeout(() => {
      const sampleBooks = [
        { id: 1, title: 'To Kill a Mockingbird', author: 'Harper Lee', category: 'Fiction', price: 12.99, stock: 45 },
        { id: 2, title: '1984', author: 'George Orwell', category: 'Fiction', price: 10.99, stock: 32 },
        { id: 3, title: 'The Great Gatsby', author: 'F. Scott Fitzgerald', category: 'Fiction', price: 11.50, stock: 27 },
        { id: 4, title: 'Clean Code', author: 'Robert C. Martin', category: 'Programming', price: 35.99, stock: 15 },
        { id: 5, title: 'The Pragmatic Programmer', author: 'Andrew Hunt', category: 'Programming', price: 39.99, stock: 12 }
      ];
      setBooks(sampleBooks);
      setLoading(false);
    }, 1000);
  }, []);

  // Filter books based on search term
  const filteredBooks = books.filter(book => 
    book.title.toLowerCase().includes(searchTerm.toLowerCase()) || 
    book.author.toLowerCase().includes(searchTerm.toLowerCase()) ||
    book.category.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="books-page">
      <div className="container">
        <div className="page-header">
          <h1>Books Management</h1>
          <button className="btn-add">
            <i className="fas fa-plus"></i> Add New Book
          </button>
        </div>
        
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search for books by title, author, or category..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <i className="fas fa-search search-icon"></i>
        </div>
        
        {loading ? (
          <div className="loading">Loading books...</div>
        ) : (
          <BookList books={filteredBooks} />
        )}
      </div>
    </div>
  );
};

export default BooksPage;
